package Class007;

public class ForLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// wanna print Hello 5 times.
	
	
		     // data type ..  condition..     incrementation
		
		//      ^               ^                ^
		for (int a=1;          a<=5;           a++ ) {
		
		System.out.println("Hello");
	}
	
	
		for (int c = 2; c<=20; c ++) {
			System.out.println("Hi");
		}
	}

}



