package Class008;

public class NestedLoopExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// car
	

		// multiplication table
		for (int i = 1; i <= 9; i++) {

			for (int j = 1; j <= 9; j++) {
				System.out.println(i + " x " + j + " = " + i * j);
			}

			System.out.println(" ---------------  ");
		}

	}

}
