package Class04;

public class EvenOrOdds {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		// Even or Odds numbers
		// Write a Java program to check if the Number is even or odd.
		
		int number=6;
		System.out.println(5%2);
		
		// if we take the modulus of a number by2 and the answer is 0, it is an even number
		//otherwise its an odd number.
		
		if (number%2==0)
		{ System.out.println("the number is even");}
		else {System.out.println("the number is odd");}
	}

}
