package com.syntax.class01;

public class Homework2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Primitive whole number values;
		
		byte    box1 = 23;
		short   box2 = 230;
		int     box3 = 23000;
		long    box4 = 230000000L;
		
		// Primitive decimal values;
		
		float   Variable1 = 2.3f;   // don't forget to add letter f for float values
		double  Variable2 = 23.0;  
		
		// Single character values
		
		char container1 = 'a';  // for single characters or symbols use single quote;
        char percentage = '%';
        
        // Storing Yes or No values
        
        boolean interesting = true; // no brackets or quote needed boolean values;
        boolean boring = false; 
		
        // Printing the above values;
        
        System.out.println(box1);
        System.out.println(box3);
        System.out.println(Variable1);
        System.out.println(Variable2);
        System.out.println(container1);
        System.out.println(percentage);
        System.out.println(interesting);
        System.out.println(boring);
      
		
	}

}
