package com.syntax.class02;

public class VariableStudentInfo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name = "Mansoor";
		String surname = "Raufi";
		char grade = 'B';
		String city = "Atlanta";
		String State = "Ga";
		String Phone = "1234567890";

		System.out.println("My name is " + name + " my last name is " + surname);
		System.out.println("I live in " + city + State);
		System.out.println("My phone number is " + Phone);
		System.out.println("I am " + grade + " grade sudent");

	}

}
